export default function authPopup( props ) {

    function handleSubmit() {
       props.onSubmit(); 
    }

    return (
      <div className="auth-popup">
        <h2 className="auth-popup__header">{props.title}</h2>
        <form className="auth-popup__form" name={`${props.name}-form`} onSubmit={handleSubmit}>
          {props.children}
          <button
            className="auth-popup__save-btn-auth"
            type="submit"
          >
            {props.btnText}
          </button>
        </form>
      </div>
    );
  }
