import loaderImgFailure from '../images/AuthImgFailure.png';
import loaderImgSuccess from '../images/AuthImgSuccess.png';

function InfoTooltip(props) {                  
  const infoText = "Что-то пошло не так! Попробуйте еще раз.";
  //infoText = props.isSuccess?"Вы успешно заргистрировались!":"Что-то пошло не так! Попробуйте еще раз."
  return (                
    <div className={`popup ${props.isOpen && " popup_opened"}`}>
      <div className="popup__container">        
        <img className="popup__auth-res-img" src={loaderImgSuccess} alt="Изображение статуса успешности авторизации"/>
        <h2 className="popup__infotext">{infoText}</h2>
        <button className="popup__close-icon"  type="button" onClick={props.onClose}></button>        
       </div>
    </div>
 )
}

export default InfoTooltip;
