import Logo from '../images/logo.svg';
import React from "react";
import { Switch, Route, NavLink } from "react-router-dom";
import { useState, useEffect } from "react";

function Header(props) {
  //const email = "asfedoroff@gmail.com"; //=props.email;
  const email = props.email;

  function handleLogout() {
    props.onLogout();
  }

  return (
    <header className="header">        
      <img src={Logo} className="header__logo" alt="Логотип"/>
      <p className="header__email">{email}</p>
      <Route path="/sign-in">
        <NavLink to="sign-in" className="header__loginout-lnk">Регистрация</NavLink>
      </Route>
      <Route path="/sign-in">
        <NavLink to="sign-in" className="header__loginout-lnk">Выйти</NavLink>
      </Route>
      <Route exact path="/">
        <button 
          className="header__loginout-lnk"
          onClick={handleLogout}>
          Выйти
        </button>
      </Route>
    </header>
  );
}

export default Header;
