function PopupWithForm(props) {            
    return (
    <div className={`popup popup_${props.name}`}>
      <div className="popup__container">
        <h2 className="popup__title">{props.title}</h2>
        <button className={`popup__close-icon popup__close-icon_${props.name}`} type="button"></button>
        <form className="popup__form" name={`popup-form-${props.name}`} novalidate>
        {props.children}
         <button className={`popup__save-btn popup-save-btn_${props.name}`} type="submit">Сохранить</button>        
        </form>
       </div>
    </div>
 )
}

export default PopupWithForm;
