//asf
//import React from 'react';
import { useState, useEffect } from 'react';
import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import Loader from './Loader';
import AddPlacePopup from './AddPlacePopup';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import ImagePopup from './ImagePopup';
import Api from '../utils/api';
import CurrentUserContext from "../contexts/CurrentUserContext.js";

function App() {

  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  const [loaderState, setLoader] = useState({isOpen:true});
  const [selectedCard, setSelectedCard] = useState(null);  
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);  
  const [isDataRetrieving, setIsDataRetrieving] = useState(false);

  function handleEditProfileClick () {
    setEditProfilePopupOpen(!isEditProfilePopupOpen);    
  }

  function handleAddPlaceClick () {
    setAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleCardClick(card) {
    setSelectedCard(card)    
  }

  function handleUpdateAvatar(data) {
    setIsDataRetrieving(true);
    Api
     .editAvatar(data)
     .then((res) => {
       setCurrentUser(res);
       closeAllPopups();
     })
      .catch((err) => {
        setLoader({isOpen:true, errMsg:err});
     })
      .finally(() => {
        setIsDataRetrieving(false);
     });
  }

  function handleUpdateUser(data) {
    setIsDataRetrieving(true);
    Api
      .editUserInfo(data)
      .then((res) => {        
        setCurrentUser(res);
        closeAllPopups();      
    })
    .catch((err) => {
      setLoader({isOpen:true, errMsg:err});
    })
    .finally(() => {
      setIsDataRetrieving(false);
    });
  }  

  function handleCardLike(card) {        
    const isLiked = card.likes.some(i => i._id === currentUser._id);       
    Api
      .toggleLike(card._id, isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    })
    .catch((err) => {
      console.log(err);
    })
  } 

  function handleCardDelete (card) {
    Api
      .deleteCard(card._id)
      .then(() => {      
        setCards((state) => state.filter((c) => c._id !== card._id));
      })
      .catch((err) => {
        console.log(err);
      })
  }

  function handleAddPlace(data) {
    setIsDataRetrieving(true);
    Api
      .addCard(data)
      .then((res) => {        
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((err) => {
        //console.log(`Ошибка: ${err}`);
        setLoader({isOpen:true, errMsg:err});
      })
      .finally(() => {
        setIsDataRetrieving(false);
    });
  }

  function closeAllPopups() {      
    setEditProfilePopupOpen(false);
    setEditAvatarPopupOpen(false);
    setAddPlacePopupOpen(false);
    setSelectedCard(null);
  }

  useEffect(() => {    
    Promise.all([
        Api.getUserInfo(),
        Api.getCards()
      ])    
      .then((data) => {
        const [userData, cards] = data;
        setCurrentUser(userData);        
        setCards(cards);        
     })
     .catch((err) => {
      console.log(err);
    })
    .finally(() => {
      setLoader({isOpen:false, errMsg:''});
    })
  }, [])

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div>
        <Header />
        <Main 
          onEditProfileClick={handleEditProfileClick}
          onAddPlaceClick={handleAddPlaceClick}
          onEditAvatarClick={handleEditAvatarClick}
          onCardClick={handleCardClick}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
          cards={cards}
        />
        <Footer />
        <Loader isOpen={loaderState.isOpen} errMsg={loaderState.errMsg}/>
        <EditProfilePopup 
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          isDataRetrieving={isDataRetrieving}
        />
        <AddPlacePopup 
          isOpen={isAddPlacePopupOpen} 
          onClose={closeAllPopups}
          onAddPlace={handleAddPlace}
          isDataRetrieving={isDataRetrieving}
        /> 
        <EditAvatarPopup 
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
          isDataRetrieving={isDataRetrieving}
        />

        <div className="popup popup_delete-card">
        <div className="popup__container">
          <button className="popup__close-icon popup__close-icon_confirm-delete" type="button"></button>
            <h2 className="popup__title popup__title_confirm-delete">Вы уверены?</h2>
            <form className="popup__form popup__form_confirm-delete" name="confirm-delete" noValidate>              
                <button type="submit" className="popup__save-btn popup__save-btn_confirm-delete">Да</button>
            </form>
        </div>
      </div>
      <ImagePopup card={selectedCard} onClose={closeAllPopups}/>
    </div>
  </CurrentUserContext.Provider>
  );
}

export default App;
