export default function authPopup( props ) {

  function handleSubmit() {
    props.onSubmit(); 
  }

  return (
    <div className="popup popup_auth popup_opened">
      <div className="popup__container">
        <h2 className="popup__title popup__title_auth">{props.title}</h2>
        <form className="popup__form" name={`${props.name}-form`} onSubmit={handleSubmit}>
          {props.children}
          <button
            className="popup__save-btn-auth"
            type="submit"
          >
            {props.btnText}
          </button>
        </form>
      </div>
    </div>
  );
}
